import axios from "axios";
import { createContext, useEffect, useState } from "react";



export let CategoryContext = createContext()
  
export default function CategoryContextProvider (props){

    let [ categoryId , setCategoryId] = useState(null) 
  
    useEffect(() => console.log(categoryId), [categoryId]);
  
     function getAllCategories() {
     return axios.get(`https://ecommerce.routemisr.com/api/v1/categories`);
    }
    function getCategoryId(id){
      setCategoryId(id)
    }

    async function getAllSubCategoriesOnCategory(categoryId) {
        return await axios.get(`https://ecommerce.routemisr.com/api/v1/categories/${categoryId}/subcategories`);
        }
      




    return <CategoryContext.Provider value={{getAllCategories,getCategoryId,getAllSubCategoriesOnCategory}}>
        {props.children}
    </CategoryContext.Provider>
}
