import axios from "axios";
import React from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
export default function Footer() {
  function allSubCategories() {
    return axios.get(`https://ecommerce.routemisr.com/api/v1/subcategories`);
  }
  let { isLoading, data } = useQuery("subcategories", () => allSubCategories());
  console.log(data?.data);
  return (
    <>
      {isLoading ? (
        <>
          <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
            <InfinitySpin width="200" color="rgb(24, 7, 109)" />
          </div>{" "}
        </>
      ) : (
        <div className="container-fluid bg-main p-2 mt-4">
          <div className="row text-branch ">
            <div className="col-md-2 ">
              <h6>ELECTRONICS</h6>
            </div>
            <div className="col-md-2 ">
              <h6>FASHION</h6>
            </div>{" "}
            <div className="col-md-2 ">
              <h6>HOME AND KITCHEN</h6>
            </div>{" "}
            <div className="col-md-2 ">
              <h6>BEAUTY</h6>
            </div>{" "}
            <div className="col-md-2 ">
              <h6>KIDS, BABY & TOYS</h6>
            </div>{" "}
            <div className="col-md-2 ">
              <h6>ELECTRONICS</h6>
            </div>
            {data?.data.data.map((category) => {
              return (
                <>
                  {
                    <div key={category._id} className="col-md-2 p-2">
                      <h6 className=" text-light ">{category.name}</h6>
                    </div>
                  }
                </>
              );
            })}
          </div>
        </div>
      )}
      <div className="container-fluid bg-body-secondary">
        <div className="row d-flex align-items-center">
          <div className="col-md-4">
            <ul className="nav  justify-content-start  p-4  ">
              <li className=" text-main">
                <h6>© 2023 noon. All Rights Reserved</h6>
              </li>
            </ul>
          </div>
          <div className="col-md-8 ">
            <div className="nav align-items-center text-main  justify-content-end  p-4 ">
              
                <h6 className="text-main p-2">Careers</h6>
              
              
                <h6 className="p-2">Warranty Policy</h6>
              
              
                <h6 className="p-2">Sell with us</h6>
              
              
                <h6 className="p-2 disabled">Terms of Use</h6>
              
              
                <h6 className="p-2 disabled">Terms of Sale</h6>
              
              
                <h6 className="p-2 disabled">Privacy Policy</h6>
              
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
