import { useContext, useEffect, useState } from "react";
import CategorySlider from "../CategorySlider/CategorySlider";
import axios from "axios";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
import { CategoryContext } from "../Context/CategoryContext";

export default function Categories() {
  let {isLoading} = useQuery("featuredCategories", getAllCategories);
 let {getCategory} =  useContext(CategoryContext)
 let [categoryDetails, setCategoryDetails] = useState(null);
 useEffect(() => console.log(categoryDetails), [categoryDetails]);

async function getAllCategories ()
{
  let {data} = await getCategory ()
  setCategoryDetails(data?.data)

}





  return (
    <>
      <CategorySlider />

      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container   py-2 ">
          <div className="row">
            {categoryDetails.map((category) =>( 
              <div key={category._id} className="container category col-md-2" width={90}>
                 <Link >
                 <div
                   className="row cursor-pointer py-3 px-2"
                   >
                  
                   <img
                     className="w-100"
                     src={category.image}
                     alt={category.title}
                   />
                   <div className="text-main font-sm fw-bolder mt-2">
                     {category.name}
                   </div>
                
                 </div>

             </Link>
             <button className="btn bg-main">category id</button>
 
               </div>
            ))};
          </div>
        </div>
      )}




      
    </>
  );
}
