import axios from "axios";
import {  useEffect } from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";


export default function SubCategories() {

  let {isLoading, data } = useQuery(
    "featuredSubCategories",
    getAllSubCategories
  );

  function getAllSubCategories() {
   return axios.get(`https://ecommerce.routemisr.com/api/v1/subcategories`);
  }



  useEffect(() => {
    getAllSubCategories();
  }, []);
  return (
    <>
      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container   py-2 ">
          <div className="row">
            {data?.data.data.map((subcategory) => (
              <div key={subcategory._id} className="product col-md-2" width={90}>
                <Link  to={`/Home/${subcategory._id}`}>
                <div
                  className=" cursor-pointer py-3 px-2"
                >
                  <div className="text-main  font-sm fw-bolder mt-2">
                    {subcategory.name}
                  </div>
                  <div className="text-main  font-sm fw-bolder mt-2">
                    {subcategory.slug}
                  </div>
                  <div className="text-main  font-sm fw-bolder mt-2">
                    {subcategory.category}
                  </div>
                  <button  className="btn text-light bg-main">get category</button>
                </div>
                
            </Link>

              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}
