  return  axios.get("https://ecommerce.routemisr.com/api/v1/categories");
  
}
let { isLoading, isError, data } = useQuery("getAllCategory", getAllCategory);
console.log(data?.data.data);
  return <>
  {data?.data.data?data?.data.data.map((category)=><div className="bg-danger">{category.image}</div>):""}
  
  </>;
}
