import axios from "axios";
import { createContext , useEffect, useState} from  "react";
import { useQuery } from "react-query";

export let ProductApiContext = createContext();

export default function ProductApiContextProvider(props){
  let { isError, isFetching, isLoading, data , refetch } = useQuery(
    "featuredProducts",
    getAllProducts
  );

  function getAllProducts() {
    return axios.get(`https://ecommerce.routemisr.com/api/v1/products`);
  }
  useEffect(() => {
    getAllProducts();
  }, []);
    
  return <ProductApiContext.Provider >
      {props.children}
    </ProductApiContext.Provider>
}
