import axios from 'axios'
import React, { useEffect } from 'react'
import { InfinitySpin } from 'react-loader-spinner'
import { useQuery } from 'react-query'
import { useParams } from 'react-router-dom'
export default function ProductDetails() {
  
  let params = useParams()

  function getProductDetails (id){
    return  axios.get(`https://ecommerce.routemisr.com/api/v1/products/${id}`)
  } 

  let {isError , isLoading ,data} = useQuery('productDetails',()=> getProductDetails(params.id))
  console.log(data?.data.data);
  
  return (
    <>
    {isLoading ? <><div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
        </>:
        <><div className='container'>
        <div className='row'>
          <div className='col-md-3 '>
<img className='w-100' src={data?.data.data.imageCover} alt={data?.data.data.title}/>
            </div>
          <div className='col-md-9 bg-main '><h1>category info</h1></div>
        </div>
      </div></>
        }

    </>
  )
}
