import CategorySlider from "../CategorySlider/CategorySlider";
import axios from "axios";
import {  useEffect } from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";


export default function Categories() {

  let {isLoading, data } = useQuery(
    "featuredCategories",
    getAllCategories
  );

  function getAllCategories() {
   return axios.get(`https://ecommerce.routemisr.com/api/v1/categories`);
  }

  function getSpecificCategories(id){
    return axios.get(`https://ecommerce.routemisr.com/api/v1/categories/${id}`);
  }

  useEffect(() => {
    getAllCategories();
  }, []);
  return (
    <>
    <CategorySlider/>

      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container   py-2 ">
          <div className="row">
            {data?.data.data.map((category) => (
              <div key={category._id} className="product col-md-2" width={90}>
                <Link  to={`/Home/${category._id}`}>
                <div
                  className=" cursor-pointer py-3 px-2"
                >
                  <div ><img w-100 src={category.image} alt = {category.name}/> </div>
                  <div className="text-main  font-sm fw-bolder mt-2">
                    {category.name}
                  </div>
           
                  <div className="text-main  font-sm fw-bolder mt-2">
                    {category.category}
                  </div>
                  <button onClick={()=>getSpecificCategories(category._id)}   className="btn text-light bg-main">get category</button>
                </div>
                
            </Link>

              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}
