import axios from "axios";
import React from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { useParams } from "react-router-dom";
export default function ProductDetails() {
  let params = useParams();


  function getAllBrandProducts() {
    return axios.get(
      `https://ecommerce.routemisr.com/api/v1/products`
    );
  }

  let { isLoading, data } = useQuery("allBrandProducts", () =>
  {  getAllBrandProducts(params.id)
    console.log(data)}
  )
  return (
    <>
      {isLoading ? (
        <>
          <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
            <InfinitySpin width="200" color="rgb(24, 7, 109)" />
          </div>
        </>
      ) : (
        <>
          <div className="container text-main mt-5">
            <div >
              {data?.data.data.map((product) => {
                return <>
                <div>
                  {product.name}
                </div>
                </>
                 })}
            </div>
          </div>
        </>
      )}
    </>
  );
}
