import * as Yup from "yup";

export default function validationSchema() {
  let phoneRegex = /\(?([0-9]{3})\)?([ .-]?)([0-9]{3})\2([0-9]{4})/;
  const schema = Yup.object({
    name: Yup.string()
      .min(3, "name must be at least 3 characters ")
      .max(10, "name must be at most 10 characters ")
      .required("name is required"),
    email: Yup.string().email
      .min(3, "email must be 3 characters long")
      .max(10, "email must be 10 characters long")
      .required("email required"),
    phone:Yup.string().matches(phoneRegex, "Wrong Number").required("phone required"),
    password: Yup.string().matches(/^[A-Z][a-z0-9]{5-10}$/),
    rePassword: Yup.string().onOf([
      Yup.ref("password ", "RePassword dose not match password"),
    ]),
  });
}
