import axios from "axios";
import { useContext } from "react";
import {  InfinitySpin } from "react-loader-spinner";
import { useNavigate } from "react-router-dom";
import { ProductApiContext } from "../Context/ProductApiContext";

export default function Product() {
  let navigate = useNavigate();
let { isLoading , data} = useContext(ProductApiContext)
  
  return (
    <>
 
      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container   py-2 ">
          <h1>featured Products</h1>
          <div className="row">
            {data?.data.data.map((product) => (
              <div key={product.id} className="col-md-2  ">
                <div className="product cursor-pointer py-3 px-2" onClick={()=>navigate('/ProductDetails')}>
                  <img
                    className="w-100"
                    src={product.imageCover}
                    alt={product.title}
                  />
                  <div className="text-danger font-sm fw-bolder mt-2">
                    {product.category.name}
                  </div>
                  <div className="text-main font-sm fw-bolder mt-2">
                    {product.title}
                  </div>

                  <div className="d-flex justify-content-between  mt-3">
                    <span>{product.price} EGP</span>
                    <span>
                      <i className="fas fa-star rating-color p-2">
                        {product.ratingsAverage}
                      </i>
                    </span>
                  </div>
                  <button className="btn bg-main text-white w-100 btn-sm mt-2">
                    Add To Cart
                  </button>
                 
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}
