import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../Context/CartContext";
export default function Cart() {


  let { getUserCartItems } = useContext(CartContext);
 let [ cartDetails , setCartDetails] =  useState(null)
  async function getCart() {
    let {data} = await getUserCartItems();
    setCartDetails(data);
  }
  useEffect(() => {
    getCart();
  }, []);
  return <>
  {cartDetails?<div className="w-75 mx-auto p-3 bg-main-light">
    <h3>Shopping Cart </h3>
    <h4 className="h6 text-main fw-bolder">Cart Items :{cartDetails.numOfCartItems} </h4>
    <h4 className="h6 text-danger fw-bolder">Total Cart Price : {cartDetails.data.totalCartPrice} EGP </h4>
    {cartDetails.data.products.map((product) =>
    <div className="row border-bottom py-2 px-2  ">
      <div className="col-md-1"><img className="w-100" key={product.id} src={product.product.imageCover} alt="productImage" /></div>
      
      <div className="col-md-11">
        <div className="d-flex justify-content-between align-items-center">
<div>
<h4 className="h6">{product.product.title.split(' ').slice(0,3).join(' ')}</h4>
        <h4 className="h6 text-danger">{product.price}</h4>
</div>
<div>
        <button className="btn btn-brdr-main p-2">+</button>
        <span >{product.count}</span>
        <button className="btn btn-brdr-main p-2">-</button>
      </div>

      <span className="btn btn-"> </span>
        </div>
       
      </div>
    
    </div> )
      }

  </div>:''}
  
  </>;
}
