import React, { useContext, useEffect, useState } from "react";
import { WishlistContext } from "./WishlistContext";


export default function Wishlist() {
  let { getWishlist } = useContext(WishlistContext);
  let [wishlistProducts , setWishlistProducts] = useState(null)
  
  async function getWishlistItems() {
    let { data } = await getWishlist();
    setWishlistProducts(data?.data)
  }
  
  useEffect(() => {
    getWishlistItems();
  }, []);
  console.log(wishlistProducts);
  return (
    <>
{wishlistProducts?.map((product)=>(
  <div className="container mt-5" key={product._id}>
    <div className="row">
      <div className="col-md-9">
        <img width={100} src={product.imageCover} alt="" />
        <h4 className="mt-2">{product.title}</h4>
        <h4 className="mt-2 text-branch">{product.category.name}</h4>
        <h4 className="mt-2">{product.description}</h4>
        <h4 className="mt-2">{product.price}</h4>
      </div>
      <div className="col-md-3"></div>
    </div>
    </div>
))}
    </>
  );
}
