import axios from "axios";
import { useState } from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";

export default function Categories() {
  
  let {isLoading , data } = useQuery("featuredCategories", getAllBrands);
  let [brands , setBrands]=   useState(null)

  async function getAllBrands(){
    try{
      let {data} = await axios.get('https://ecommerce.routemisr.com/api/v1/brands');
      setBrands(data.data);
      console.log(data.data);
      
    }
    catch(err){
      console.log(err);
    }
  }
  

  return (
    <>

      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container animate__animated  animate__fadeIn  py-2 ">
          <div className="row">
            {data?.data.data.map((brand) =>( 
              console.log(brand),
              <div key={brand._id} className="container my-5 text-center animate__animated  animate__fadeIn  category col-md-2" width={90}>
                 <Link to={`/BrandProducts/${brand._id}`}>
                 <div
                   className="row cursor-pointer py-3 px-2"
                   >
                  
                   <img
                     className="w-100 "
                     src={brand.image}
                     alt={brand.title}
                   />
                   <div className="text-main font-sm fw-bolder mt-2">
                     {brand.name}
                   </div>
                
                 </div>

             </Link>
             <button  className="btn bg-main m-2 text-light">Show All</button>
 
               </div>
            ))};
          </div>
        </div>
      )}




      
    </>
  );
}
