import axios from "axios";
import React from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { useParams } from "react-router-dom";
export default function Footer() {
  let params = useParams();

  async function getAllSubCategoriesOnCategory() {
    let { data } = await axios.get(
      `https://ecommerce.routemisr.com/api/v1/subcategories`
    );
    console.log(data.data);
  }

  let { isLoading, data } = useQuery("categoryDetails", () =>
    getAllSubCategoriesOnCategory()
  );
  return (
    // <>
    //   <footer className="bg-main-light  py-5">
    //     <div className="container-fluid bg-danger">
    //       <div className="row">
    //         <div className="col-md-2">
    //           <h6>ELECTRONICS</h6>
    //         </div>
    //         <div className="col-md-2">
    //           <h6>FASHION</h6>
    //         </div>
    //         <div className="col-md-2">
    //           <h6>HOME AND KITCHEN</h6>
    //         </div>
    //         <div className="col-md-2">
    //           <h6>BEAUTY</h6>
    //         </div>
    //         <div className="col-md-2">
    //           <h6>KIDS, BABY & TOYS</h6>
    //         </div>
    //         <div className="col-md-2">
    //           <h6>TOP BRANDS</h6>
    //         </div>
    //       </div>
    //     </div>
    //   </footer>
    // </>
    <>
      {isLoading ? (
        <>
          <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
            <InfinitySpin width="200" color="rgb(24, 7, 109)" />
          </div>
        </>
      ) : (
        <>
          <div className="container">
            <div className="row">
              {data?.data.map((category) => {
                console.log(category);
                <div className="col-md-2">
                  <h6>tmam</h6>
                </div>;
              })}
            </div>
          </div>
        </>
      )}
    </>
  );
}
