import React, { useContext, useState } from "react";
import logo from "../../Assets/images/pexels-andrea-piacquadio-3772506.jpg"
import { useFormik } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../Context/UserContext";

export default function Login() {
  let {setUserToken} =  useContext(UserContext)
  let validationSchema = Yup.object({
    email: Yup.string().email("email  Invalid").required("email required"),
    password: Yup.string().required("password required"),
  });

  let [error, setError] = useState(null);
  let navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  async function LoginSubmit(values) {
    setIsLoading(true);
    try {
      setIsLoading(true);
      let { data } = await axios.post(
        "https://ecommerce.routemisr.com/api/v1/auth/signin",
        values
      );
      navigate("/Home");
      localStorage.setItem("userToken", data.token);
      setUserToken(data.token);

    } catch (err) {
      setIsLoading(false);
      setError(err.response.data.message);
      console.log(err.response.data.message);
    }
  }

  let formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema,
    onSubmit: LoginSubmit,
  });
  return (
    <>
      {/* <div className="container w-75 mt-5 vh-100">
       

        <h4>Login Now</h4>
        <form classNameName="mt-2" onSubmit={formik.handleSubmit}>
          <label htmlFor="name">Email :</label>

          <input
            id="email"
            value={formik.values.email}
            onBlur={formik.handleBlur}
            classNameName=" w-100 form-control p-2"
            onChange={formik.handleChange}
            name="email"
            type="email"
          />
          {formik.errors.email && formik.touched.email ? (
            <div classNameName="alert alert-danger mt-2">{formik.errors.email}</div>
          ) : (
            ""
          )}

          <label htmlFor="name">Password :</label>

          <input
            id="password"
            value={formik.values.password}
            onBlur={formik.handleBlur}
            classNameName=" w-100 form-control"
            onChange={formik.handleChange}
            name="password"
            type="password"
          />
          {formik.errors.password && formik.touched.password ? (
            <div classNameName="alert alert-danger mt-2">
              {formik.errors.password}
            </div>
          ) : (
            ""
          )}

          {isLoading ? (
            <button type="button" className="btn bg-main my-2 text-white">
              <i className="fas fa-spider fa-spin"></i>
            </button>
          ) : (
            <div className="d-flex align-items-center">
              <button
                disabled={!(formik.isValid && formik.dirty)}
                type="submit"
                className="btn bg-main my-3 mx-2 text-white"
              >
                Login
              </button>{" "}
              <Link to={'/Register'} className="btn bg-main text-white">Register Now</Link>
            </div>
            
          )}
          <div className="d-flex  justify-content-center">
           {error !== null ? (
          <div className="alert alert-danger text-center w-50 ">{error}</div>
        ) : (
          ""
        )}
        </div>
        </form>
      </div> */}



<section className="vh-100" style={{backgroundColor:"#9A616D"}}>
  <div className="container py-5 h-100">
    <div className="row d-flex justify-content-center align-items-center h-100">
      <div className="col col-xl-10">
        <div className="card" style={{borderRadius: "1rem"}}>
          <div className="row g-0">
            <div className="col-md-6 col-lg-5 d-none d-md-block">
              <img  src={logo}
                alt="login form" style={{borderRadius: "1rem 0 0 1rem;" , width:"490px" , height:"650px"} } />
            </div>
            <div className="col-md-6 col-lg-7 d-flex align-items-center">
              <div className="card-body p-4 p-lg-5 text-black">

                <form onSubmit={formik.handleSubmit}>

                  <div className="d-flex align-items-center mb-3 pb-1">
                    <i className="fas fa-cubes fa-2x me-3" style={{color:"#ff6219"}}></i>
                    <span className="h1 fw-bold mb-0">Logo</span>
                  </div>

                  <h5 className="fw-normal mb-3 pb-3" style={{letterSpacing:"1px"}}>Sign into your account</h5>

                  <div className="form-outline mb-4">
                  <label htmlFor="name">Email :</label>

                    <input
                    value={formik.values.email}
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    name="email"
                    type="email" 
                    id="form2Example17" 
                    className="form-control form-control-lg" />
                    <label className="form-label" for="form2Example17">Email address</label>
                  </div>
                  {formik.errors.email && formik.touched.email ? (
            <div classNameName="alert alert-danger mt-2">{formik.errors.email}</div>
          ) : (
            ""
          )}

                  <div className="form-outline mb-4">
                  <label htmlFor="name">Password :</label>

                    <input 
                    id="password"
                    value={formik.values.password}
                    onBlur={formik.handleBlur}
                    type="password" 
                    onChange={formik.handleChange}
                    name="password"
                    // id="form2Example27" 
                    className="form-control form-control-lg" />
                    
                  </div>
                  {formik.errors.password && formik.touched.password ? (
            <div classNameName="alert alert-danger mt-2">
              {formik.errors.password}
            </div>
          ) : (
            ""
          )}
                  {isLoading ? (
            <button type="button" className="btn bg-main my-2 text-white">
              <i className="fas fa-spider fa-spin"></i>
            </button>
          ) : (
            <div className="d-flex align-items-center">
              <button
                disabled={!(formik.isValid && formik.dirty)}
                type="submit"
                className="btn bg-main my-3 mx-2 text-white"
              >
                Login
              </button>{" "}
              <Link to={'/Register'} className="btn bg-main text-white">Register Now</Link>
            </div>
            
          )}
          <div className="d-flex  justify-content-center">
           {error !== null ? (
          <div className="alert alert-danger text-center w-50 ">{error}</div>
        ) : (
          ""
        )}
                </div>

                </form>

              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
    </>
  );
}
