import CategorySlider from "../CategorySlider/CategorySlider";
import axios from "axios";
import { useEffect } from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";

export default function Categories() {
  let { isLoading, data } = useQuery("featuredCategories", getAllCategories);

  function getAllCategories() {
    return axios.get(`https://ecommerce.routemisr.com/api/v1/categories`);
  }

  function getSpecificCategories(id) {
    return axios.get(`https://ecommerce.routemisr.com/api/v1/categories/${id}`);
  }

  useEffect(() => {
    getAllCategories();
  }, []);
  return (
    <>
      <CategorySlider />

      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container   py-2 ">
          <div className="row">
            {data?.data.data.map((category) => (
                 <div key={category.id} className="container category col-md-2" width={90}>
                 <Link  to={`/ProductDetails/${category._id}`}>
                 <div
                   className="row cursor-pointer py-3 px-2"
                 >
                  
                   <img
                     className="w-100"
                     src={category.image}
                     alt={category.title}
                   />
                   <div className="text-main font-sm fw-bolder mt-2">
                     {category.name}
                   </div>
                   
                 </div>
                 <Link
                   onClick={() => getAllCategories(category.id)}
                   className="btn bg-main text-white w-100 btn-sm mt-2"
                 >
                   Add To Cart
                 </Link>
             </Link>
 
               </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}
