import axios from "axios";
import React from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";
export default function Footer() {


    function allSubCategories() {
   return axios.get(
      `https://ecommerce.routemisr.com/api/v1/subcategories`
    );
    
    
}
let { isLoading, data } = useQuery("subcategories", () =>
allSubCategories()
);
console.log(data?.data);
  return (
 <>
 {isLoading?
 <>
<div className="loading-page w-100 d-flex align-items-center justify-content-center ">
            <InfinitySpin width="200" color="rgb(24, 7, 109)" />
          </div> </>
 :
<div className="container-fluid bg-main p-2 mt-4">
    <div className="row text-branch ">
        <div className="col-md-2 ">
        <h6>ELECTRONICS</h6>
        </div>
        <div className="col-md-2 ">
        <h6>FASHION</h6>
        </div>     <div className="col-md-2 ">
        <h6>HOME AND KITCHEN</h6>
        </div>     <div className="col-md-2 ">
        <h6>BEAUTY</h6>
        </div>     <div className="col-md-2 ">
        <h6>KIDS, BABY & TOYS</h6>
        </div>     <div className="col-md-2 ">
        <h6>ELECTRONICS</h6>
        </div>
    {data?.data.data.map((category) => {
                return <>{
                  
            
              <div key={category._id} className="col-md-2 p-2">
                  <h6 className=" text-light ">{category.name}</h6>
              </div>
              
                }</>;
              })}

    </div>

</div> 



 }
<ul className="nav  justify-content-end bg-body-secondary p-5  ">
  <li className="nav-item text-main">
    <Link className="nav-link active" >Careers</Link>
  </li>
  <li className="nav-item">
    <Link className="nav-link">Warranty Policy
</Link>
  </li>
  <li className="nav-item">
    <Link className="nav-link" >Sell with us</Link>
  </li>
  <li className="nav-item">
    <Link className="nav-link disabled" >Terms of Use</Link>
  </li> 
  <li className="nav-item">
    <Link className="nav-link disabled" >Terms of Sale</Link>
  </li>
   <li className="nav-item">
    <Link className="nav-link disabled" >Privacy Policy
</Link>
  </li>
</ul>

 </>
  );
}
