import React, { useContext, useEffect } from "react";
import { WishlistContext } from "./WishlistContext";
import { useQuery } from "react-query";
import { InfinitySpin } from "react-loader-spinner";

export default function Wishlist() {

let {getWishlist } =  useContext(WishlistContext)   
let { isLoading, data } = useQuery("WishlistProducts", getWishlistItems);

async function getWishlistItems() {
  let { data } = await getWishlist();
  console.log(data.data);
}


useEffect(()=>{getWishlistItems()},[])
  return (
    <>
    {isLoading? (
     <div className="loading-page mt-5 w-100 vh-100  d-flex justify-content-center align-items-center  ">
          <InfinitySpin width="200" height={1000} color="rgb(24, 7, 109)" />
        </div> ) :<>
        {data?.data.map((product)=>{
          console.log(product);
          <h6>{product._id}</h6>
        })}
        </>
        }
    </>
  );
}
