import React, { useContext, useEffect, useState } from "react";
import { WishlistContext } from "./WishlistContext";
import { Link } from "react-router-dom";
import { InfinitySpin } from "react-loader-spinner";

export default function Wishlist() {
  let { getWishlist  , deleteFromWishlist} = useContext(WishlistContext);
  let [wishlistProducts, setWishlistProducts] = useState(null);

  async function getWishlistItems() {
    let { data } = await getWishlist();
    setWishlistProducts(data?.data);
  }
  async function deleteProductFromWishlist(productId) {
    let {data}  = await deleteFromWishlist(productId);
    setWishlistProducts(data?.data);
    
  }

  useEffect(() => {
    getWishlistItems();
  }, []);
  console.log(wishlistProducts);
  return (
    <>
     {/* {wishlistProducts?.map((product) => (
        <div className="container  mt-5" key={product._id}>
          <div className="row d-flex justify-content-center  align-items-center">
                <h4 className=" text-branch">Title : </h4>
            <div className="col-md-3 text-main">
              <img className="w-75 " src={product.imageCover} alt="" />
            </div>
            <div className="col-md-9">
              <div className="d-flex  align-items-center ">
                <h4 className="mt-2 mx-1"> {product.title}</h4>
              </div>
              <div className="d-flex  align-items-center">
                <h4 className="text-branch">Description : </h4>
                <h4 className="mt-2 mx-1">{product.description?.split(" ").slice(0, 2).join(" ")}</h4>
              </div>
              <div className="d-flex  align-items-center">
                {" "}
                <h4 className="text-branch">Category Of :</h4>
                <h4 className="mt-2 mx-1">{product.category?.name}</h4>
              </div>
              <div className="d-flex  align-items-center">
                <h4 className="text-branch">Price : </h4>
                <h4 className="mt-2 mx-1">{product.price} EGP</h4>
              </div>
            </div>
          <button onClick={()=>deleteProductFromWishlist(product._id)} className="btn bg-main text-light ">Delete</button>
          </div>
        </div>
      ))} 
       */}
        {wishlistProducts ? (
        <div  className="w-75 mx-auto p-5 bg-main-light mt-5 animate__animated  animate__fadeIn">
          <h3>Wishlist </h3>
          
          <h4 className="h6 text-branch fw-bolder">
            Total Cart Price : {wishlistProducts.price} EGP{" "}
          </h4>
          {wishlistProducts?.map((product) => (
            <div key={product._id} className="row border-bottom  py-2 px-2  ">
              <div className="col-md-1">
                <img
                  className="w-100"
                  src={product.imageCover}
                  alt="productImage"
                  />
              </div>

              <div   className="col-md-11">
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <h4 className="h6">
                      {product.title.split(" ").slice(0, 3).join(" ")}
                    </h4>
                    <h4 className="h6 ">{product.price} :EGP</h4>
                  </div>
                  <div>
                   
                  </div>
                </div>
                <span className="btn  text-light ">
                  <i onClick={()=>deleteProductFromWishlist(product._id)} className="text-branch fas fa-trash-can"></i>{" "}
                </span>

              </div>
            </div>
          ))}
          <Link  to={'/Address'} className="btn bg-main w-25  p-1 mt-4 text-light">Online payment</Link>

        </div>
      ) : (
        <div className="loading-page w-100 vh-100 d-flex justify-content-center align-items-center">
        <InfinitySpin width="200" color="rgb(24, 7, 109)" />
      </div>
      )}
    </>
  );
}
