import axios from "axios";
import { useContext, useEffect } from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";


export default function Brands() {

  let {isLoading, data } = useQuery(
    "featuredProducts",
    getAllBrands
  );

  function getAllBrands() {
    return axios.get(`https://ecommerce.routemisr.com/api/v1/brands`);
  }


  useEffect(() => {
    getAllBrands();
  }, []);
  return (
    <>
      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container   py-2 ">
          <div className="row">
            {data?.data.map((brand) => (
              
              <div key={brand.id} className="product col-md-2" width={90}>
                <Link  to={`/ProductDetails/${brand.id}`}>
                <div
                  className=" cursor-pointer py-3 px-2"
                >
                  <img
                    className="w-100"
                    src={brand.image}
                    alt={brand.title}
                  />
                  <div className="text-main font-sm fw-bolder mt-2">
                    {brand.category.name}
                  </div>
                  <div className="text-main font-sm fw-bolder mt-2">
                    {brand.title.split(" ").slice(0, 3).join(" ")}
                  </div>

                  <div className="d-flex  justify-content-between align-items-center  mt-3">
                    <span className="text-dark">{brand.price} EGP</span>
                    <span className="text-main">
                      <i className="fas fa-star p-1 text-branch ">
                      </i>
                        {brand.ratingsAverage}
                    </span>
                  </div>
                  
                </div>
                {/* <Link
                  onClick={() => addbrandToCart(brand.id)}
                  className="btn bg-main text-white w-100 btn-sm mt-2"
                >
                  Add To Cart
                </Link> */}
            </Link>

              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}
