import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../Context/CartContext";
export default function Cart() {


  let { getUserCartItems } = useContext(CartContext);
 let [ cartDetails , setCartDetails] =  useState(null)
  async function getCart() {
    let {data} = await getUserCartItems();
    setCartDetails(data);
  }
  useEffect(() => {
    getCart();
  }, []);
  return <>
  {cartDetails?<div className="w-75 mx-auto p-3 bg-main-light">
    <h3>Shopping Cart </h3>
    <h4 className="h6 text-main fw-bolder">Cart Items :{cartDetails.numOfCartItems} </h4>
    <h4 className="h6 text-danger fw-bolder">Total Cart Price : {cartDetails.data.totalCartPrice} EGP </h4>
    {cartDetails.data.products.map((product) =><div className="row">
    <img key={product.id} src={product.product.imageCover} alt="productImage" />
    </div> )
      }

  </div>:''}
  
  </>;
}
