import { Link, useNavigate } from "react-router-dom";
import logo from "../../Assets/images/Web capture_17-11-2023_19475_.jpeg";
import { useContext } from "react";
import { UserContext } from "../Context/UserContext";

export default function Navbar() {
  let { userToken, setUserToken } = useContext(UserContext);

  let navigate = useNavigate();

  function logout() {
    localStorage.removeItem("userToken");
    setUserToken(null);
    navigate("/Login");
  }
  return (
    <div>
    <div className="p-0 min-vh-100 bg-dark">
        <ul className="text-light list-unstyled">

            <li className="p-3 pe-lg-2 d-lg-flex d-none  ">
            <i className="fa-regular fa-note-sticky text-info fs-2"></i>                           
            <p className='ps-3 fs-4'>Notes</p>
            </li>
            
            <li className="p-3 pe-lg-5 sidebar-element">
                <Link to="/home" className="nav-link px-0 px-lg-2"> <i className="bi-house"/><span className="px-lg-2 ms-1 d-none d-lg-inline">Home</span> </Link>
            </li>
          
            <li className="p-3 pe-lg-5 sidebar-element" onClick={logout}>
                <Link to="/login" className="nav-link px-0 px-lg-2"> <i className="bi bi-box-arrow-left"></i><span className="px-lg-2 ms-1 d-none d-lg-inline">Logout</span> </Link>
            </li>

        </ul>
    </div>
</div>
  );
}
