import React, { useContext, useEffect, useState } from "react";
import { WishlistContext } from "./WishlistContext";
import { Link } from "react-router-dom";
import { InfinitySpin } from "react-loader-spinner";

export default function Wishlist() {
  let { getWishlist , deleteFromWishlist } = useContext(WishlistContext);
  let [wishlistProducts,setWishlistProducts] = useState(null);

  async function getProductWishlist() {
    let { data } = await getWishlist();
    setWishlistProducts(data);
  }
  
  
  async function deleteProductFromWishlist(id) {
    let {data}  = await deleteFromWishlist(id);
    setWishlistProducts(data);
    
  }
  // async function updateCount(id , count) {
  //   let {data}  = await updateProductCount(id , count);
  //   setCartDetails(data);
    
  // }
  useEffect(() => {
    getProductWishlist();
  }, []);

  
  return (
    <>
    {wishlistProducts?.data? 
    <>
    {wishlistProducts?.data?.map((product)=>{
      console.log(product);
      return(
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <img width={250} src={product.imageCover} alt="productImageCover" />
            </div>
            <div className="col-md-9 mt-5">
              <h4>{product.sold}</h4>
              <h4>{product.description}</h4>
              <h4>{product.price}</h4>
            <button onClick={()=>{deleteProductFromWishlist(product._id)}} className="btn bg-main text-light">Delete</button>
            </div>
          </div>
        </div>
      )
    })}
    </>

    
    :console.log("msh tmmam")}
    
    </>
  );
}
