import React, { useContext, useEffect, useState } from "react";
import { WishlistContext } from "./WishlistContext";
import { Link } from "react-router-dom";
import { InfinitySpin } from "react-loader-spinner";

export default function Wishlist() {
  let { getWishlist, deleteFromWishlist } = useContext(WishlistContext);
  let [wishlistProducts, setWishlistProducts] = useState(null);

  async function getWishlistItems() {
    let { data } = await getWishlist();
    setWishlistProducts(data);
  }
  async function deleteProductFromWishlist(id) {
    let { data } = await deleteFromWishlist(id);
    setWishlistProducts(data);
  }
  
  useEffect(() => {
    getWishlistItems();
    console.log(wishlistProducts);
  }, []);
  
  return (
    <>
{wishlistProducts?
<>
{wishlistProducts?.data.map((product)=>(
  <div className="row bg-main">
    <div className="col-md-3 "><img width={200} src={product.imageCover} alt="ProductImage" /></div>
    <div className="col-md-9   mt-5">
      <h6>title : {product.title}</h6>
      <h6>description : {product.description}</h6>
      <h6>price : {product.price}</h6>
      <h6>priceAfterDiscount : {product.priceAfterDiscount}</h6>
    </div>
    <button onClick={()=>{deleteProductFromWishlist(product._id)}} className="btn bg-main text-light">Delete From Wishlist</button>
  </div>
))}
</>:''}
    </>
  );
}
