import axios from "axios";
import { useEffect } from "react";
import {  InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { useNavigate } from "react-router-dom";

export default function Category() {
  let navigate = useNavigate();

  let { isError, isFetching, isLoading, data , refetch } = useQuery(
    "featuredCategorys",
    getAllCategorys,{
      // cacheTime:3000,
      // refetchInterval:5000,
      enabled:false
    }
  );
  console.log(data?.data.data);

  function getAllCategorys() {
    return axios.get(`https://ecommerce.routemisr.com/api/v1/category`);
  }
  useEffect(() => {
    getAllCategorys();
  }, []);
  return (
    <>
     <button className="btn bg-main text-white w-100 btn-sm mt-2" onClick={()=>refetch()}>
                    get all category
                  </button>
      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container  py-2 ">
          <h1>featured category's</h1>
          <div className="row">
            {data?.data.data.map((category) => (
              <div key={category.id} className="col-md-2">
                <div className="category cursor-pointer py-3 px-2">
                  <img
                    className="w-100"
                    src={category.imageCover}
                    alt={category.title}
                  />
                  <div className="text-danger font-sm fw-bolder mt-2">
                    {category.category.name}
                  </div>
                  <div className="text-main font-sm fw-bolder mt-2">
                    {category.title}
                  </div>

                  <div className="d-flex justify-content-between  mt-3">
                    <span>{category.price} EGP</span>
                    <span>
                      <i className="fas fa-star rating-color p-2">
                        {category.ratingsAverage}
                      </i>
                    </span>
                  </div>
                  <button className="btn bg-main text-white w-100 btn-sm mt-2">
                    Add To Cart
                  </button>
                 
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}
