import React, { useContext, useState } from "react";
import Style from "./Login.module.css";
import { useFormik } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../Context/UserContext";
let userContext =  UserContext(UserContext)

export default function Login() {
  let validationSchema = Yup.object({
    email: Yup.string().email("email  Invalid").required("email required"),
    password: Yup.string().required("password required"),
  });

  let [error, setError] = useState(null);
  let navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  async function LoginSubmit(values) {
    setIsLoading(true);
    try {
      setIsLoading(true);
      let { data } = await axios.post(
        "https://note-sigma-black.vercel.app/api/v1/users/signIn",
        values
      );
      navigate("/Home");
      localStorage.setItem("userToken", data.token);

    } catch (err) {
      setIsLoading(false);
      setError(err.response.data.msg);
    }
  }

  let formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema,
    onSubmit: LoginSubmit,
  });
  return (
    <>
      <div className="container w-75 mt-5">
       

        <h4>Login Now</h4>
        <form onSubmit={formik.handleSubmit}>
          <label htmlFor="name">Email :</label>

          <input
            id="email"
            value={formik.values.email}
            onBlur={formik.handleBlur}
            className=" w-100 form-control p-2"
            onChange={formik.handleChange}
            name="email"
            type="email"
          />
          {formik.errors.email && formik.touched.email ? (
            <div className="alert alert-danger mt-2">{formik.errors.email}</div>
          ) : (
            ""
          )}

          <label htmlFor="name">Password :</label>

          <input
            id="password"
            value={formik.values.password}
            onBlur={formik.handleBlur}
            className=" w-100 form-control"
            onChange={formik.handleChange}
            name="password"
            type="password"
          />
          {formik.errors.password && formik.touched.password ? (
            <div className="alert alert-danger mt-2">
              {formik.errors.password}
            </div>
          ) : (
            ""
          )}

          {isLoading ? (
            <button type="button" className="btn bg-main my-2 text-white">
              <i className="fas fa-spider fa-spin"></i>
            </button>
          ) : (
            <div className="d-flex align-items-center">
              <button
                disabled={!(formik.isValid && formik.dirty)}
                type="submit"
                className="btn bg-main my-3 mx-2 text-white"
              >
                Login
              </button>{" "}
              <Link to={'/Register'} className="btn bg-main text-white">Register Now</Link>
            </div>
            
          )}
          <div className="d-flex  justify-content-center">
           {error !== null ? (
          <div className="alert alert-danger text-center w-50 ">{error}</div>
        ) : (
          ""
        )}
        </div>
        </form>
      </div>
    </>
  );
}
