import React from "react";
import Style from "./CategorySlider.module.css";
import { useQuery } from "react-query";
import axios from "axios";
import Slider from "react-slick";
import { InfinitySpin } from "react-loader-spinner";

export default function CategorySlider() {
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 7,
    slidesToScroll: 1,
    autoplay: true,
    speed: 2000,
    autoplaySpeed: 2000,
    cssEase: "linear"
  };

  function getAllCategory() {
    return axios.get("https://ecommerce.routemisr.com/api/v1/categories");
  }
  let { isLoading, isError, data } = useQuery("getAllCategory", getAllCategory);
  console.log(data?.data.data);
  return (
    <>
    {isLoading?  <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>:<> {data?.data.data ? (<Slider {...settings}>
          {data?.data.data.map((category) => ( 
            <div >
              <img key={category.id} height={200} className="w-100" src={category.image} alt={category.name}></img>
            </div>
          ))}
        </Slider>
      ) : (
        ""
      )}</>}
     
    </>
  );
}
