import CategorySlider from "../CategorySlider/CategorySlider";
import axios from "axios";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { Link } from "react-router-dom";

export default function Categories() {
  
  let {isLoading , data } = useQuery("featuredCategories", getAllBrands);


   function getAllBrands() {
   return axios.get(`https://ecommerce.routemisr.com/api/v1/brands`);
  }

  return (
    <>

      {isLoading ? (
        <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin width="200" color="rgb(24, 7, 109)" />
        </div>
      ) : (
        <div className="container   py-2 ">
          <div className="row">
            {data?.data.data.map((brand) =>( 
              <div key={brand._id} className="container py-5 text-center  category col-md-2" width={90}>
                 <Link to={`/BrandProducts/${brand._id}`}>
                 <div
                   className="row cursor-pointer py-3 px-2"
                   >
                  
                   <img
                     className="w-100 "
                     src={brand.image}
                     alt={brand.title}
                   />
                   <div className="text-main font-sm fw-bolder mt-2">
                     {brand.name}
                   </div>
                
                 </div>

             </Link>
             <button  className="btn bg-main m-2 text-light">brand id</button>
 
               </div>
            ))};
          </div>
        </div>
      )}




      
    </>
  );
}
