import React from "react";
import Style from "./Register.module.css";
import { useFormik } from "formik";
import * as Yup from "yup";
export default function Register() {


  let phoneRegex = /\(?([0-9]{3})\)?([ .-]?)([0-9]{3})\2([0-9]{4})/;
  let validationSchema = Yup.object({
    name: Yup.string().min(3, "name must be at least 3 characters ").max(10, "name must be at most 10 characters ").required("name is required"),
    email: Yup.string().email(3, "email must be 3 characters long").max(10, "email must be 10 characters long").required("email required"),
    phone:Yup.string().matches(phoneRegex, "Wrong Number").required("phone required"),
    password: Yup.string().matches(/^[A-Z][a-z0-9]{5-10}$/),
    rePassword: Yup.string().onOf([
      Yup.ref("password ", "RePassword dose not match password"),
    ]),
  });

  let formik = useFormik({
    initialValues: {
      name: "",
      email: "",
      phone: "",
      password: "",
      rePassword: "",
    },
    validationSchema,
    onSubmit: (values) => console.log(values),

  });
  return (
    <>
      <div className="container w-75">
        <h4>Register Now</h4>
        <form onSubmit={formik.handleSubmit}>
          <label htmlFor="name">Name :</label>
          <input
            id="name"
            value={formik.values.name}
            onBlur={formik.handleBlur}
            className=" w-100 form-control"
            onChange={formik.handleChange}
            name="name"
            type="name"
          />
          {formik.errors.name && formik.touched.name ?<div className="alert alert-danger mt-2"></div> :'' }
          
          <label htmlFor="name">Email :</label>

          <input
            id="email"
            value={formik.values.email}
            onBlur={formik.handleBlur}
            className=" w-100 form-control"
            onChange={formik.handleChange}
            name="email"
            type="email"
          />
          <label htmlFor="name">Password :</label>

          <input
            id="password"
            value={formik.values.password}
            onBlur={formik.handleBlur}
            className=" w-100 form-control"
            onChange={formik.handleChange}
            name="password"
            type="password"
          />
          <label htmlFor="name">Re Password :</label>

          <input
            id="rePassword"
            value={formik.values.rePassword}
            onBlur={formik.handleBlur}
            className=" w-100 form-control"
            onChange={formik.handleChange}
            name="rePassword"
            type="password"
          />
          <label htmlFor="name">Phone Number :</label>

          <input
            id="phone"
            value={formik.values.phone}
            onBlur={formik.handleBlur}
            className=" w-100 form-control"
            onChange={formik.handleChange}
            name="phone"
            type="tel"
          />
          <button disabled={!(formik.isValid && formik.dirty)} type="submit" className="btn bg-main my-2 text-white">
            Register
          </button>
        </form>
      </div>
    </>
  );
}
