import React from "react";

import logo from '../../Assets/images/banner-4.jpeg'
import Slider from "react-slick";
import { InfinitySpin } from "react-loader-spinner";

export default function MainSlider() {
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 7,
    slidesToScroll: 1,
    autoplay: true,
    speed: 2000,
    autoplaySpeed: 2000,
    cssEase: "linear"
  };

  
  return (
    <>
  <div className="loading-page w-100 d-flex align-items-center justify-content-center ">
          <InfinitySpin  />
        </div>
        <Slider {...settings}>
         
              <img height={200} className="w-100" src={logo} ></img>
        </Slider>
      
     
    </>
  );
}
