import axios from "axios";
import React, { useEffect, useState } from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
import { useParams } from "react-router-dom";
export default function ProductDetails() {
  let params = useParams();


  function getAllSubCategoriesOnCategory(categoryId) {
    return axios.get(
      `https://ecommerce.routemisr.com/api/v1/categories/${categoryId}/subcategories`
    );
  }

  let { isLoading, data } = useQuery("categoryDetails", () =>
    getAllSubCategoriesOnCategory(params.id)
  );
  // console.log(data?.data.data[0].name);

  return (
    <>
      {isLoading ? (
        <>
          <div classNameName="loading-page w-100 d-flex align-items-center justify-content-center ">
            <InfinitySpin width="200" color="rgb(24, 7, 109)" />
          </div>
        </>
      ) : (
        <>
          <div classNameName="container text-main mt-5">
            <div >
              {data?.data.data.map((category) => {
                return <>
               <div className="container bg-main">
               <div className="card w-25 mt-5 d-flex">
  <div className="card-body">
    <h5 className="card-title">{category.name}</h5>
    <h6 className="card-subtitle mb-2 text-muted">{category.slug}</h6>
    <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
    
  </div>
</div>
               </div>



                </>
                 })}
            </div>
          </div>
        </>
      )}
    </>
  );
}
