import axios from "axios";
import React from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";

export default function AllOrders() {
  let { isLoading, data } = useQuery("allOrders", getAllOrders);
   function getAllOrders() {
    return axios.get( `https://ecommerce.routemisr.com/api/v1/orders`

    );
    // console.log(data.data);
  }
  return (
    <>
      {isLoading ? (
        <>
          <div className="loading-page w-100 vh-100  d-flex justify-content-center align-items-center ">
            <InfinitySpin width="200" color="rgb(24, 7, 109)" />
          </div>
        </>
      ) : (
        <div className="container">
          <div className="row">
            {data?.data.map((order) => (
             
              <div key={order._id} className="row border-bottom py-2 px-2 ">
                <div className="col-md-9">
                    <h4>hello</h4>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </>
  );
}
