import axios from "axios"
import { useEffect, useState } from "react"
import { useQuery } from "react-query"

export default function Product() {

  
  
  let {isError,isFetching,isLoading,data} = useQuery('featuredProducts', getAllProducts ) 
  console.log(data?.data.data);

  function getAllProducts() {
     
    return  axios.get(`https://ecommerce.routemisr.com/api/v1/products`)
   
    }
  useEffect(()=>{
    getAllProducts()
  },[])
  return (
    <>
    {/* {isLoading ? } */}
    <div className="container text-center py-2 ">
      <h1>featured Products</h1>
      <div className="row">
      {data?.data.data.map((product)=> <div key={product.id} className="col-md-2">
        <div className="product cursor-pointer py-3 px-2">
        <img className="w-100" src={product.imageCover} alt={product.title}/>
        <div className="text-danger font-sm fw-bolder mt-4">{product.category.slug}</div>
        {/* <h3 className="h6">{product.subcategory.slug}</h3> */}

        <div className="d-flex justify-content-between  mt-3">
          <span>{product.price} EGP</span>
          <span><i className="fas fa-star rating-color p-2">{product.ratingsAverage}</i></span>
        </div>
        <button className="btn bg-main text-white w-100 btn-sm mt-2">Add To Cart</button>
        </div>






      </div>)}
        

      </div>
    </div>

    </>
  )
}
