import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { jwtDecode } from "jwt-decode"
export default function AllOrders() {
    let [orders , setOrders] = useState(null) 
    let encodedToken = localStorage.getItem('userToken')
    let decodedToken = jwtDecode(encodedToken)
    let userId = decodedToken.id

    async function getAllOrders(){
        let {data} = await axios.get(`https://ecommerce.routemisr.com/api/v1/orders/user/${userId}`)
        setOrders(data)
      }
      
      useEffect(()=>{getAllOrders()},[])
      console.log(orders);
  return (
    <>
    {orders?.map((orders)=>{return( <div key={orders._id} className="container vh-100">
            <div  className="row mt-5">
              <div className="col-md-9 ">
                <h5>taxPrice: {orders.taxPrice}</h5>
                <h5 className="">shippingPrice: {orders.shippingPrice}</h5>
                <h5 className=" text-branch ">
                totalOrderPrice: {orders.totalOrderPrice}
                </h5>
                  <h5 className="text-main">
                  paymentMethodType: {orders.paymentMethodType} 
                  </h5>
                <div className="d-flex justify-content-between">
                <h5 className="text-main">
                 customer name: {orders.user.name}
                  </h5>
                  
                </div>
                <div className="d-flex justify-content-between">
                <h5 className="text-main">
                 phone number: {orders.user.phone}
                  </h5>
                  
                </div>
                <div className="d-flex justify-content-between">
                <h5 className="text-main">
                : {orders.user.name}
                  </h5>
                  
                </div>
                
              </div>
            </div>
        </div>)
       

    })}
    
    </>
  )
}
