import React, { useContext, useEffect, useState } from "react";
import { WishlistContext } from "./WishlistContext";

export default function Wishlist() {
  let { getWishlist  , deleteFromWishlist} = useContext(WishlistContext);
  let [wishlistProducts, setWishlistProducts] = useState(null);

  async function getWishlistItems() {
    let { data } = await getWishlist();
    setWishlistProducts(data?.data);
  }
  async function deleteProductFromWishlist(productId) {
    let {data}  = await deleteFromWishlist(productId);
    setWishlistProducts(data?.data);
    
  }

  useEffect(() => {
    getWishlistItems();
  }, []);
  console.log(wishlistProducts);
  return (
    <>
     {wishlistProducts?.map((product) => (
        <div className="container  mt-5" key={product._id}>
          <div className="row d-flex justify-content-center  align-items-center">
            <div className="col-md-3 text-main">
              <img className="w-75 " src={product.imageCover} alt="" />
            </div>
            <div className="col-md-9">
              <div className="d-flex  align-items-center ">
                <h4 className=" text-branch">Title : </h4>
                <h4 className="mt-2 mx-1"> {product.title}</h4>
              </div>
              <div className="d-flex  align-items-center">
                <h4 className="text-branch">Description : </h4>
                <h4 className="mt-2 mx-1">{product.description?.split(" ").slice(0, 2).join(" ")}</h4>
              </div>
              <div className="d-flex  align-items-center">
                {" "}
                <h4 className="text-branch">Category Of :</h4>
                <h4 className="mt-2 mx-1">{product.category?.name}</h4>
              </div>
              <div className="d-flex  align-items-center">
                <h4 className="text-branch">Price : </h4>
                <h4 className="mt-2 mx-1">{product.price} EGP</h4>
              </div>
          <button onClick={()=>deleteProductFromWishlist(product._id)} className="btn bg-main text-light ">Delete</button>
            </div>
          </div>
        </div>
      ))} 
      
    </>
  );
}
