import axios from "axios";
import React from "react";
import { InfinitySpin } from "react-loader-spinner";
import { useQuery } from "react-query";
export default function Footer() {


    function getAllSubCategoriesOnCategory() {
   return axios.get(
      `https://ecommerce.routemisr.com/api/v1/subcategories`
    );
    
    
}
let { isLoading, data } = useQuery("categoryDetails", () =>
getAllSubCategoriesOnCategory()
);
console.log(data?.data);
  return (
 <>
 {isLoading?
 <>
<div className="loading-page w-100 d-flex align-items-center justify-content-center ">
            <InfinitySpin width="200" color="rgb(24, 7, 109)" />
          </div> </>
 :
<div className="container-fluid bg-main p-4 mt-4">
    <div className="row text-light">
        <div className="col-md-2">
        <h4>hi</h4>
        </div>
        <div className="col-md-2">
        <h4>hi</h4>
        </div>     <div className="col-md-2">
        <h4>hi</h4>
        </div>     <div className="col-md-2">
        <h4>hi</h4>
        </div>     <div className="col-md-2">
        <h4>hi</h4>
        </div>     <div className="col-md-2">
        <h4>hi</h4>
        </div>
    {data?.data.data.map((category) => {
                return <>{
                  
            
              <div key={category._id} className="col-md-2 ">
                  <h6 className=" text-branch ">{category.name}</h6>
              </div>
              
                }</>;
              })}

    </div>
</div> 
 }


 </>
  );
}
